import Box from '@mui/material/Box'
import React, { useCallback, useEffect, useState } from 'react'
import { makeStyles } from '@mui/styles'
import { Grid } from '@mui/material'
import Button from '@mui/material/Button'
import { observer } from 'mobx-react'
import { styled } from '@mui/material/styles'
import Paper from '@mui/material/Paper'
import operation from '../../constans/dataRedux'
import { buttonOperations, buttons, buttonZero } from '../../constans/colorSchemeButtons'
import operations from '../../constans/operations'
import numericStore from '../../store/numericStore'

const useStyles = makeStyles({
  display: {
    fontSize: 28,
    width: 'auto',
    whiteSpace: 'normal',
    textAlign: 'right',
    fontWeight: 300,
    height: 90,
  },
})

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
  border: '4px solid #1d2352',
  backgroundColor: '#1d2352',
  width: 270,
}))

export const Calculator = observer(() => {
  const classes = useStyles()

  const checkPoint = useCallback((value) => {
    if (operations.includes(value)) setDisabled(false)
    else if (value === ',') setDisabled(true)
  }, [])

  return (
    <Item>
      <Box
        className={classes.display}
        component="span"
        sx={{
          bgcolor: '#B0D6DE', border: 1, borderRadius: 4, display: 'block', color: 'black', marginBottom: 1,
        }}
      >
        {numericStore.counter}
      </Box>
    </Item>
  )
})

export default Calculator
