import {
  acNumOperation,
  delplyNumOperation,
  evlaNumOperation,
  minusplyNumOperation,
  multiplyNumOperation, plusminNumOperation, plusNumOperation, pointNumOperation, procentNumOperation,
} from '../actions/actions'
import Fetch from '../scripts/dataFetch'
import GetRequest from '../scripts/GetRequest'
import OperationSign from '../scripts/PutAway'
import retObjType from '../scripts/valuesNumOperation'

const initialState = {
  addHistory: [],
  history: [],
  counter: '',
}

const numReducer = (state = initialState, action) => {
  const operationSign = new OperationSign()
  switch (action.type) {
    case retObjType(action.type)().type:
      return {
        ...state,
        addHistory: [],
        counter: `${state.counter}${retObjType(action.type)().value}`,
      }
    case plusNumOperation:
      return {
        ...state,
        addHistory: [],
        counter: `${operationSign.putAway(state.counter)}+`,
      }
    case acNumOperation:
      return {
        ...state,
        addHistory: [],
        counter: '',
      }
    case multiplyNumOperation:
      return {
        ...state,
        addHistory: [],
        counter: `${operationSign.putAway(state.counter)}*`,
      }
    case minusplyNumOperation:
      return {
        ...state,
        addHistory: [],
        counter: `${operationSign.putAway(state.counter)}-`,
      }
    case delplyNumOperation:
      return {
        ...state,
        addHistory: [],
        counter: `${operationSign.putAway(state.counter)}/`,
      }
    case pointNumOperation:
      return {
        ...state,
        addHistory: [],
        counter: `${operationSign.putAway(state.counter)}.`,
      }
    case procentNumOperation:
      return {
        ...state,
        addHistory: [],
        counter: `${operationSign.putAway(state.counter)}%`,
      }
    case plusminNumOperation:
      if (state.counter[state.counter.length - 1] === '+') {
        return {
          ...state,
          addHistory: [],
          counter: `${state.counter.slice(0, state.counter.length - 1)}-`,
        }
      } if (state.counter[state.counter.length - 1] === '-') {
        return {
          ...state,
          addHistory: [],
          counter: `${state.counter.slice(0, state.counter.length - 1)}+`,
        }
      }
      break
    case evlaNumOperation:
      Fetch('/', 'POST', state.counter, operationSign.putFirst(operationSign.endValue(state.counter)))
      GetRequest.getHistory().then((data) => (
        { addHistory: state.addHistory.push(...data) }
      ))
      return {
        history: state.addHistory,
        counter: operationSign.putFirst(operationSign.endValue(state.counter)),
      }
    default:
      return state
  }
  return 0
}

export default numReducer
