import _, {lastIndexOf} from 'lodash'
import { string } from 'mathjs'
import Fetch from '../scripts/dataFetch'
import deleteRequest from '../scripts/deleteHistory'
import OperationSign from '../scripts/PutAway'
import updateHistory from '../scripts/updateHistory'
import {
  AC,
  DELETEHISTORY,
  DEPLY,
  EVAL,
  MINUS,
  MULTIPLY,
  NUM,
  PERSENT,
  PLUS,
  PLUSMIN,
  POINT,
  SHOWRENEWHISTORY,
} from './actions/variables'

const initialState = {
  disabledPoint: false,
  updateNedeed: 0,
  addHistory: [],
  history: [],
  counter: '0',
}

const foundSign = (str) => {
  const indexStatement = []
  indexStatement.push(lastIndexOf(str, '+'))
  indexStatement.push(_.findLastIndex(str, '-'))
  indexStatement.push(_.findLastIndex(str, '/'))
  indexStatement.push(_.findLastIndex(str, '*'))
  const index = indexStatement.filter((i) => i >= 0)
  console.log(index, indexStatement)
  return index[0]
}

const changeSign = (state) => {
  const sign = ['+', '-', '/', '*']
  const lengthCounter = state.counter.length
  foundSign(state.counter)
  if (state.counter[lengthCounter - 1] === '-') {
    const historyCounter = state.counter.slice(0, lengthCounter - 1)
    return {
      ...state,
      addHistory: [],
      disabledPoint: false,
      counter: `${historyCounter}+`,
    }
  } if (state.counter[lengthCounter - 1] === '+') {
    const historyCounter = state.counter.slice(0, lengthCounter - 1)
    return {
      ...state,
      addHistory: [],
      disabledPoint: false,
      counter: `${historyCounter}-`,
    }
  } if (!state.counter[lengthCounter - 1].includes(sign)) {
    const indexSign = foundSign(state.counter)
    console.log(indexSign)
  }
  return {
    ...state,
  }
}

const numReducer = (state = initialState, action) => {
  const operationSign = new OperationSign()
  switch (action.type) {
    case NUM: {
      if (state.counter === '0' && state.counter.length === 1) {
        return {
          ...state,
          addHistory: [],
          counter: `${state.counter.slice(1, 2)}${action.number}`,
        }
      }
      return {
        ...state,
        addHistory: [],
        counter: `${state.counter}${action.number}`,
      }
    }
    case PLUS:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}+`,
      }
    case AC:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: '0',
      }
    case MULTIPLY:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}*`,
      }
    case MINUS:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}-`,
      }
    case DEPLY:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}/`,
      }
    case POINT:
      return {
        ...state,
        disabledPoint: true,
        addHistory: [],
        counter: `${operationSign.pointEnd(state.counter)}.`,
      }
    case PERSENT:
      return {
        ...state,
        addHistory: [],
        counter: `${operationSign.putAway(state.counter)}%`,
      }
    case PLUSMIN:
      return changeSign(state)
    case EVAL:
      Fetch('/', 'POST', state.counter, operationSign.putFirst(operationSign.endValue(state.counter)))
      // eslint-disable-next-line no-case-declarations
      const result = operationSign.putFirst(operationSign.endValue(state.counter))
      return {
        ...state,
        updateNedeed: state.updateNedeed + 1,
        disabledPoint: false,
        counter: result,
      }
    case DELETEHISTORY:
      console.log(action.expression, 1)
      deleteRequest(action.expression, action.result)
      return {
        ...state,
        updateNedeed: state.updateNedeed + 1,
        history: updateHistory(state.history),
      }
    case SHOWRENEWHISTORY:
      return {
        ...state,
        history: action.data,
      }
    default:
      return state
  }
}

export default numReducer
