import { makeAutoObservable } from 'mobx'
import { evaluate } from 'mathjs'
import { forbiddenFirstCharacter, forbiddenLastCharacter, replacementSign } from '../helpers/changesOperationsMath'
import deleteRequest from '../api/apiDeletehistory'
import updateHistory from '../helpers/updateHistory'
import getHistoryApi from '../api/apiResendHistory'
import saveData from '../api/apiSaveHistory'

const foundSignIndex = (str) => {
  const arr = []
  for (let i = str.length; i > 0; i -= 1) {
    if (str[i] === '+' || str[i] === '-'
      || str[i] === '*' || str[i] === '/') {
      arr.push(i)
    }
  }
  return arr[0]
}

const inspectionSigns = (_counter) => {
  const indexSign = foundSignIndex(_counter)
  const sliceHistoryStart = _counter.slice(0, indexSign + 1)
  const sliceHistoryEnd = _counter.slice(indexSign + 1, _counter.length)
  if (indexSign) {
    if (_counter[indexSign - 1] === '(') {
      const sliceStartPlus = sliceHistoryStart.slice(0, indexSign - 1)
      const sliceEndPlus = sliceHistoryEnd.slice(0, sliceHistoryEnd.length - 1)
      return `${sliceStartPlus}${sliceEndPlus}`
    }
    return `${sliceHistoryStart}(-${sliceHistoryEnd})`
  }
  return _counter
}

const changeSign = (_counter) => {
  const sign = ['+', '-', '/', '*']
  const lengthCounter = _counter.length
  if (_counter[lengthCounter - 1] === '-') {
    const historyCounterPlusEnd = _counter.slice(0, lengthCounter - 1)
    return `${historyCounterPlusEnd}+`
  }
  if (_counter[lengthCounter - 1] === '+') {
    const historyCounter = _counter.slice(0, lengthCounter - 1)
    return `${historyCounter}-`
  } if (!_counter[lengthCounter - 1].includes(sign)) {
    return inspectionSigns(_counter)
  }
  return _counter
}

class NumericStore {
  counter = '0'

  history = []

  constructor() {
    makeAutoObservable(this)
  }

  numericOperation = (number) => {
    if (this.counter === '0' && this.counter.length === 1) {
      this.counter = `${this.counter.slice(1, 2)}${number}`
    } else this.counter = `${this.counter}${number}`
  }

  plusOperation = () => {
    this.counter = `${replacementSign(this.counter)}+`
  }

  minusOperation = () => {
    this.counter = `${replacementSign(this.counter)}-`
  }

  deleteAllOperation = () => {
    this.counter = '0'
  }

  multiplyOperation = () => {
    this.counter = `${replacementSign(this.counter)}*`
  }

  devideOperation = () => {
    this.counter = `${replacementSign(this.counter)}/`
  }

  pointOperation = () => {
    this.counter = `${replacementSign(this.counter)}.`
  }

  percentOperation = () => {
    const indexSign = foundSignIndex(this.counter.slice(0, this.counter.length - 1))
    const startHistory = this.counter.slice(0, indexSign)
    const percent = this.counter.slice(indexSign + 1, this.counter.length)
    const resultBeforePercent = evaluate(startHistory.slice(0, startHistory.length - 1))
    const resultCounter = evaluate((resultBeforePercent / 100) * percent)
    this.counter = resultCounter
  }

  evalOperation = () => {
    const result = forbiddenFirstCharacter((forbiddenLastCharacter(this.counter)))
    saveData(this.counter, result)
      .then(() => {
        this.showNewHistory()
      })
    this.counter = result
  }

  changeSign = () => {
    this.counter = changeSign(this.counter)
  }

  deleteHistoryApi = (expression, result, index) => {
    console.log(index)
    deleteRequest(expression, result)
      .then(() => {
        this.history = updateHistory(this.history, index)
      })
      .catch((error) => {
        console.log(error)
      })
  }

  showNewHistory = () => {
    getHistoryApi()
      .then((value) => {
        this.history = value.data
      })
  }
}

export default new NumericStore()
