import Box from '@mui/material/Box'
import React, { useCallback, useState } from 'react'
import { makeStyles } from '@mui/styles'
import { Grid } from '@mui/material'
import Button from '@mui/material/Button'
import { useDispatch, useSelector } from 'react-redux'
import operation from '../constans/dataRedux'
import { buttonOperations, buttons, buttonZero } from '../constans/colorSchemeButtons'
import { evalNumOperation, valueOperation } from '../redux/actions/actions'
import operations from '../constans/operations'
import { forbiddenFirstCharacter, forbiddenLastCharacter } from '../helpers/changesOperationsMath'

const useStyles = makeStyles({
  display: {
    fontSize: 28,
    width: 'auto',
    whiteSpace: 'normal',
    textAlign: 'right',
    fontWeight: 300,
    height: 90,
  },
})

export const Calc = () => {
  const [disabled, setDisabled] = useState(false)
  const classes = useStyles()
  const dispatch = useDispatch()
  const store = useSelector((state) => state.numReducer)

  const checkPoint = useCallback((value) => {
    if (operations.includes(value)) setDisabled(false)
    else if (value === ',') setDisabled(true)
  }, [])

  const handleClick = (event, recovery) => function () {
    checkPoint(event)
    console.log(store.counter)
    if (typeof event === 'number') dispatch(valueOperation(event))
    else if (event === '=') {
      dispatch(evalNumOperation(store.counter,
        forbiddenFirstCharacter(forbiddenLastCharacter(store.counter))))
    } else if (recovery) dispatch(recovery())
  }

  const checkButton = useCallback((value) => {
    if (value === 0) return buttonZero
    if (typeof value === 'string') return buttonOperations
    return buttons
  }, [])

  const stateArray = useCallback(() => operation.map((val, i) => {
    const buttonSettings = checkButton(val.type)
    return (
      <Grid key={val.type} item xs={buttonSettings.size}>
        <Button
          sx={{
            bgcolor: buttonSettings.color,
            color: 'white',
            borderRadius: 2,
            borderColor: 'text.primary',
            gridRow: '5',
            width: '100%',
            gridColumn: 'span 2',
          }}
          disabled={val.type === ',' ? disabled : false}
          variant="outlined"
          size="large"
          onClick={handleClick(val.type, val.func)}
          key={i.toString()}
          className={val.classStyle}
        >
          {val.type}
        </Button>
      </Grid>
    )
  }), [disabled])

  return (
    <div style={{ border: '4px solid #1d2352', padding: 5, backgroundColor: '#1d2352' }}>
      <Box
        className={classes.display}
        component="span"
        sx={{
          bgcolor: '#B0D6DE', border: 1, borderRadius: 4, display: 'block', color: 'black', marginBottom: 1,
        }}
      >
        {store.counter}
      </Box>
      <Grid container rowSpacing={1} columnSpacing={{ xs: 1 }}>
        {stateArray()}
      </Grid>
    </div>
  )
}

export default Calc
