import React, { useCallback, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { showRenewHistory } from '../redux/actions/actions'

const History = () => {
  const { history } = useSelector((counter) => counter.numReducer)
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(showRenewHistory())
  }, [history])

  const rerender = useCallback(() => {
    if (history.length === 0) return <div>История пуста</div>
    return history.map((value) => (
      <div>
        {value}
      </div>
    ))
  }, [history])

  return (
    <div>
      {rerender()}
    </div>
  )
}

export default History
