import React, { useCallback, useEffect } from 'react'
import { Grid } from '@mui/material'
import { styled } from '@mui/material/styles'
import Paper from '@mui/material/Paper'
import Button from '@mui/material/Button'
import { useDispatch, useSelector } from 'react-redux'
import Calc from './components/Calc'
import {
  valueOperation,
  // acNumOperation,
  // delplyNumOperation,
  // evalNumOperation,
  // minusplyNumOperation,
  // multiplyNumOperation,
  // plusminNumOperation,
  // plusNumOperation,
  // pointNumOperation,
  // persentNumOperation,
} from './redux/actions/actions'

import operationArrayray from './data/dataRedux'
// import functions from './data/functions'
// import typeOperation from './scripts/buttonCalback'
import History from './components/History'
// import ButtonJSX from './scripts/retButJSX'
import getHistory from './scripts/GetRequest'

const App = () => {
  // eslint-disable-next-line react/destructuring-assignment
  /* let blockedPoint = props.counter.disabledPoint
  useEffect(() => {
    blockedPoint = props.disabledPoint
  // eslint-disable-next-line react/destructuring-assignment
  }, [blockedPoint])*
  // const funcOperation = functions(props)
  const dispatch = useDispatch()

  const stateArray = operationArrayray.map((val, i) => {
    const valType = typeOperation(val.type)
    if (val.type === '0') return ButtonJSX.zero(funcOperation[val.data], i, val)
    if (valType) return ButtonJSX.greyButton(funcOperation[val.data], i, val)
    if (val.type === ',')
    return ButtonJSX.blockedPoint(blockedPoint, funcOperation[val.data], i, val)
    return ButtonJSX.rest(funcOperation[val.data], i, val)
  }) */

  const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }))
  const store = useSelector((state) => state)
  const dispatch = useDispatch()

  const handleClick = (event) => function () {
    console.log(dispatch(valueOperation(event)));
  }

  const stateArray = useCallback(() => {
    const timeValue = operationArrayray.map((val, i) => (
      <Button
        sx={{
          bgcolor: '#B0C4DE', color: 'black', borderRadius: 2, borderColor: 'text.primary', gridRow: '5', width: '100%', gridColumn: 'span 2',
        }}
        variant="outlined"
        size="large"
        onClick={handleClick(val.type)}
        key={i.toString()}
        className={val.classStyle}
      >
        {val.type}
      </Button>
    ))
    return timeValue
  }, [])

  useEffect(() => {
    dispatch(getHistory())
  }, [store.num.updateNedeed])

  return (
    <div className="App">
      <Grid container spacing={2}>
        <Grid item>
          <Item>
            <Calc values={store} operationArray={stateArray} />
          </Item>
        </Grid>
        <Grid item>
          <Item>
            <History history={store} />
          </Item>
        </Grid>
      </Grid>
    </div>
  )
}
