import Box from '@mui/material/Box'
import React, {useCallback, useEffect} from 'react'
import { makeStyles } from '@mui/styles'
import { Grid } from '@mui/material'
import operation from "../data/dataRedux";
import Button from "@mui/material/Button";
import {buttonOperations, buttons, buttonZero} from "../constans/colorSchemeButtons";
import {valueOperation} from "../redux/actions/actions";
import {useDispatch} from "react-redux";
import getHistory from "../scripts/GetRequest";

const useStyles = makeStyles({
  display: {
    fontSize: 28,
    width: 'auto',
    whiteSpace: 'normal',
    textAlign: 'right',
    fontWeight: 300,
    height: 90,
  },
})

export const Calc = ({ values }) => {
  const classes = useStyles()
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getHistory())
  }, [store.num.updateNedeed])

  const handleClick = (event, recovery) => function () {
    if (typeof event === 'number') dispatch(valueOperation(event))
    else if (recovery) dispatch(recovery())
  }

  const checkButton = useCallback((value) => {
    if (value === 0) return buttonZero
    if (typeof value === 'string') return buttonOperations
    return buttons
  }, [])

  const stateArray = useCallback(() => operation.map((val, i) => {
    const buttonSettings = checkButton(val.type)
    return (
      <Grid item xs={buttonSettings.size}>
        <Button
          sx={{
            bgcolor: buttonSettings.color,
            color: 'white',
            borderRadius: 2,
            borderColor: 'text.primary',
            gridRow: '5',
            width: '100%',
            gridColumn: 'span 2',
          }}
          variant="outlined"
          size="large"
          onClick={handleClick(val.type, val.func)}
          key={i.toString()}
          className={val.classStyle}
        >
          {val.type}
        </Button>
      </Grid>
    )
  }), [])

  return (
    <div>
      <Box
        className={classes.display}
        component="span"
        sx={{
          bgcolor: '#B0D6DE', border: 1, borderRadius: 4, display: 'block', color: 'black',
        }}
      >
        {values.num.counter}
      </Box>
      <Grid container rowSpacing={1} columnSpacing={{ xs: 1 }}>
        {stateArray()}
      </Grid>
    </div>
  )
}

export default Calc
