import { call, takeEvery } from 'redux-saga/effects'
import saveData from '../api/apiSaveData'
import { EVAL } from '../redux/actions/variables'

function* sendExpressionsSaga(action) {
  const data = yield call(saveData(action.expression, action.result))
  console.log(data)
}

function* sendExpressionsWatcher() {
  yield takeEvery(EVAL, sendExpressionsSaga)
}

export default sendExpressionsWatcher
