import { lastIndexOf } from 'lodash'
import { evaluate } from 'mathjs'
import Fetch from '../scripts/dataFetch'
import deleteRequest from '../scripts/deleteHistory'
import OperationSign from '../scripts/PutAway'
import updateHistory from '../scripts/updateHistory'
import {
  AC,
  DELETEHISTORY,
  DEPLY,
  EVAL,
  MINUS,
  MULTIPLY,
  NUM,
  PERSENT,
  PLUS,
  PLUSMIN,
  POINT,
  SHOWRENEWHISTORY,
} from './actions/variables'
import operations from '../constans/operations'

const initialState = {
  disabledPoint: false,
  updateNedeed: 0,
  addHistory: [],
  history: [],
  counter: '0',
}

const foundSignIndex = (str) => {
  const arr = []
  for (let i = str.length; i > 0; i -= 1) {
    if (str[i] === '+' || str[i] === '-'
      || str[i] === '*' || str[i] === '/') {
      arr.push(i)
    }
  }
  console.log(arr)
  return 1
}

const inspectionSigns = (state) => {
  const indexSign = foundSignIndex(state.counter)
  const sliceHistoryStart = state.counter.slice(0, indexSign + 1)
  const sliceHistoryEnd = state.counter.slice(indexSign + 1, state.counter.length)
  if (indexSign) {
    if (state.counter[indexSign - 1] === '(') {
      const sliceStartPlus = sliceHistoryStart.slice(0, indexSign - 1)
      const sliceEndPlus = sliceHistoryEnd.slice(0, sliceHistoryEnd.length - 1)
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${sliceStartPlus}${sliceEndPlus}`,
      }
    }
    return {
      ...state,
      addHistory: [],
      disabledPoint: false,
      counter: `${sliceHistoryStart}(-${sliceHistoryEnd})`,
    }
  }
  return {
    ...state,
  }
}

const changeSign = (state) => {
  const sign = ['+', '-', '/', '*']
  const lengthCounter = state.counter.length
  if (state.counter[lengthCounter - 1] === '-') {
    const historyCounterPlusEnd = state.counter.slice(0, lengthCounter - 1)
    return {
      ...state,
      addHistory: [],
      disabledPoint: false,
      counter: `${historyCounterPlusEnd}+`,
    }
  } if (state.counter[lengthCounter - 1] === '+') {
    const historyCounter = state.counter.slice(0, lengthCounter - 1)
    return {
      ...state,
      addHistory: [],
      disabledPoint: false,
      counter: `${historyCounter}-`,
    }
  } if (!state.counter[lengthCounter - 1].includes(sign)) {
    return inspectionSigns(state)
  }
  return {
    ...state,
  }
}

const numReducer = (state = initialState, action) => {
  const operationSign = new OperationSign()
  switch (action.type) {
    case NUM: {
      if (state.counter === '0' && state.counter.length === 1) {
        return {
          ...state,
          addHistory: [],
          counter: `${state.counter.slice(1, 2)}${action.number}`,
        }
      }
      return {
        ...state,
        addHistory: [],
        counter: `${state.counter}${action.number}`,
      }
    }
    case PLUS:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}+`,
      }
    case AC:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: '0',
      }
    case MULTIPLY:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}*`,
      }
    case MINUS:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}-`,
      }
    case DEPLY:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}/`,
      }
    case POINT:
      return {
        ...state,
        disabledPoint: true,
        addHistory: [],
        counter: `${operationSign.pointEnd(state.counter)}.`,
      }
    case PERSENT: {
      const indexSign = foundSignIndex(state.counter.slice(0, state.counter.length - 1))
      const startHistory = state.counter.slice(0, indexSign)
      const percent = state.counter.slice(indexSign + 1, state.counter.length)
      const resultBeforePercent = evaluate(startHistory.slice(0, startHistory.length - 1))
      const result = evaluate((resultBeforePercent / 100) * percent)
      console.log(indexSign, state.counter.slice(0, state.counter.length - 1))
      return {
        ...state,
        addHistory: [],
        counter: result,
      }
    }
    case PLUSMIN:
      return changeSign(state)
    case EVAL:
      Fetch('/', 'POST', state.counter, operationSign.putFirst(operationSign.endValue(state.counter)))
      // eslint-disable-next-line no-case-declarations
      const result = operationSign.putFirst(operationSign.endValue(state.counter))
      return {
        ...state,
        updateNedeed: state.updateNedeed + 1,
        disabledPoint: false,
        counter: result,
      }
    case DELETEHISTORY:
      deleteRequest(action.expression, action.result)
      return {
        ...state,
        updateNedeed: state.updateNedeed + 1,
        history: updateHistory(state.history),
      }
    case SHOWRENEWHISTORY:
      return {
        ...state,
        history: action.data,
      }
    default:
      return state
  }
}

export default numReducer
