import React, { useCallback, useEffect } from 'react'
import { Grid } from '@mui/material'
import { styled } from '@mui/material/styles'
import Paper from '@mui/material/Paper'
import Button from '@mui/material/Button'
import { connect, useDispatch, useSelector } from 'react-redux'
import Calc from './components/Calc'
import { valueOperation } from './redux/actions/actions'
import operation from './data/dataRedux'
import History from './components/History'
import getHistory from './scripts/GetRequest'

const App = () => {
  const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }))
  const store = useSelector((state) => state)
  const dispatch = useDispatch()

  const handleClick = (event, recovery) => function () {
    if (typeof event === 'number') dispatch(valueOperation(event))
    else if (recovery) dispatch(recovery())
  }

  const stateArray = useCallback(() => operation.map((val, i) => {
    const buttonSettings = val.type === 0
      ? { color: '#1D7373', size: 6 }
      : typeof val.type === 'string'
        ? { color: 'red', size: 3 }
        : { color: '#006363', size: 3 }
    return (
      <Grid item xs={buttonSettings.size}>
        <Button
          sx={{
            bgcolor: buttonSettings.color,
            color: 'white',
            borderRadius: 2,
            borderColor: 'text.primary',
            gridRow: '5',
            width: '100%',
            gridColumn: 'span 2',
          }}
          variant="outlined"
          size="large"
          onClick={handleClick(val.type, val.func)}
          key={i.toString()}
          className={val.classStyle}
        >
          {val.type}
        </Button>
      </Grid>
    )
  }), [])

  useEffect(() => {
    dispatch(getHistory())
  }, [store.num.updateNedeed])

  return (
    <div className="App">
      <Grid container spacing={2} sx={{ width: 300 }}>
        <Grid item>
          <Item>
            <Calc values={store} operationArray={stateArray} />
          </Item>
        </Grid>
        <Grid item>
          <Item>
            <History history={store} />
          </Item>
        </Grid>
      </Grid>
    </div>
  )
}
export default connect()(App)
