import anime from 'animejs'

const deleteAnimation = (expression, obj) => function (event) {
  const buttonAnime = event.target
  buttonAnime.
  anime({
    targets: buttonAnime,
    opacity: 0,
  })
  setTimeout(() => {
    expression(obj)
  }, 1000)
}

export default deleteAnimation
