import React, { useCallback, useEffect } from 'react'
import { observer } from 'mobx-react'
import styled from 'styled-components'
import { Button } from '@mui/material'
import numericStore from '../store/numericStore'

const Fonts = styled.div`
  font-size: 16px;
  color: #000000;
  margin-left: 30px;
`

const Main = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
`

const HistoryRender = styled.div`
  overflow: scroll;
  margin-left: 10px;
  border: 1px solid #1d2352;
`

const History = observer(() => {

  useEffect(() => {
    numericStore.showNewHistory()
  }, [])

  const fetchHistory = useCallback(() => {
    numericStore.showNewHistory()
  }, [])

  const deleteHistory = useCallback((expression, result, index) => {
    numericStore.deleteHistoryApi(expression, result, index)
  }, [])

  const history = useCallback(() => {
    if (numericStore.history) {
      return numericStore.history.map((value, index) => (
        <Main>
          <Fonts>
            {value.expression}
            {'= '}
            {value.result}
          </Fonts>
          <Button
            onClick={() => deleteHistory(value.expression, value.result, index)}
            color="error"
          >
            X
          </Button>
        </Main>
      ))
    }
    return <div>История пуста</div>
  }, [numericStore.history])

  return (
    <HistoryRender>
      {history()}
    </HistoryRender>
  )
})

export default History
