import { evaluate } from 'mathjs'
// import saveData from '../api/apiSaveData'
import deleteRequest from '../api/apiDeletehistory'
import updateHistory from '../helpers/updateHistory'
import {
  AC,
  DELETEHISTORY,
  DEPLY,
  EVAL,
  MINUS,
  MULTIPLY,
  NUM,
  PERSENT,
  PLUS,
  PLUSMIN,
  POINT,
  SHOWRENEWHISTORY,
} from './actions/variables'
import getHistoryApi from '../api/resendDataApi'
import { forbiddenFirstCharacter, forbiddenLastCharacter, replacementSign } from '../helpers/changesOperationsMath'

const initialState = {
  disabledPoint: false,
  addHistory: [],
  history: [],
  counter: '0',
}

const foundSignIndex = (str) => {
  const arr = []
  for (let i = str.length; i > 0; i -= 1) {
    if (str[i] === '+' || str[i] === '-'
      || str[i] === '*' || str[i] === '/') {
      arr.push(i)
    }
  }
  return arr[0]
}

const inspectionSigns = (state) => {
  const indexSign = foundSignIndex(state.counter)
  const sliceHistoryStart = state.counter.slice(0, indexSign + 1)
  const sliceHistoryEnd = state.counter.slice(indexSign + 1, state.counter.length)
  if (indexSign) {
    if (state.counter[indexSign - 1] === '(') {
      const sliceStartPlus = sliceHistoryStart.slice(0, indexSign - 1)
      const sliceEndPlus = sliceHistoryEnd.slice(0, sliceHistoryEnd.length - 1)
      return {
        ...state,
        counter: `${sliceStartPlus}${sliceEndPlus}`,
      }
    }
    return {
      ...state,
      counter: `${sliceHistoryStart}(-${sliceHistoryEnd})`,
    }
  }
  return {
    ...state,
  }
}

const changeSign = (state) => {
  const sign = ['+', '-', '/', '*']
  const lengthCounter = state.counter.length
  if (state.counter[lengthCounter - 1] === '-') {
    const historyCounterPlusEnd = state.counter.slice(0, lengthCounter - 1)
    return {
      ...state,
      counter: `${historyCounterPlusEnd}+`,
    }
  } if (state.counter[lengthCounter - 1] === '+') {
    const historyCounter = state.counter.slice(0, lengthCounter - 1)
    return {
      ...state,
      counter: `${historyCounter}-`,
    }
  } if (!state.counter[lengthCounter - 1].includes(sign)) {
    return inspectionSigns(state)
  }
  return {
    ...state,
  }
}

const numReducer = (state = initialState, action) => {
  switch (action.type) {
    case NUM: {
      if (state.counter === '0' && state.counter.length === 1) {
        return {
          ...state,
          counter: `${state.counter.slice(1, 2)}${action.number}`,
        }
      }
      return {
        ...state,
        addHistory: [],
        counter: `${state.counter}${action.number}`,
      }
    }
    case PLUS:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${replacementSign(state.counter)}+`,
      }
    case AC:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: '0',
      }
    case MULTIPLY:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${replacementSign(state.counter)}*`,
      }
    case MINUS:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${replacementSign(state.counter)}-`,
      }
    case DEPLY:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${replacementSign(state.counter)}/`,
      }
    case POINT:
      return {
        ...state,
        disabledPoint: true,
        addHistory: [],
        counter: `${replacementSign(state.counter)}.`,
      }
    case PERSENT: {
      const indexSign = foundSignIndex(state.counter.slice(0, state.counter.length - 1))
      const startHistory = state.counter.slice(0, indexSign)
      const percent = state.counter.slice(indexSign + 1, state.counter.length)
      const resultBeforePercent = evaluate(startHistory.slice(0, startHistory.length - 1))
      const result = evaluate((resultBeforePercent / 100) * percent)
      return {
        ...state,
        addHistory: [],
        counter: result,
      }
    }
    case PLUSMIN:
      return changeSign(state)
    case EVAL: {
      // saveData(state.counter, forbiddenFirstCharacter(forbiddenLastCharacter(state.counter)))
      const result = forbiddenFirstCharacter((forbiddenLastCharacter(state.counter)))
      return {
        ...state,
        disabledPoint: false,
        counter: result,
      }
    }
    case DELETEHISTORY:
      deleteRequest(action.expression, action.result)
        .then(() => ({
          ...state,
          updateNedeed: state.updateNedeed + 1,
          history: updateHistory(state.history),
        }))
        .catch(() => ({
          ...state,
        }))
      return false
    case SHOWRENEWHISTORY:
      getHistoryApi()
        .then((response) => {
          const { data } = response
          console.log(data)
          return {
            ...state,
            addHistory: [5],
            history: data,
          }
        })
    default:
      return state
  }
}

export default numReducer
