import {
  AC,
  EVAL,
  MINUS,
  MULTIPLY,
  PLUS,
  PLUSMIN,
  POINT,
  DELETEHISTORY,
  SHOWRENEWHISTORY,
  NUM,
  PERSENT,
  DEPLY,
} from './variables'

export const valueOperation = (value) => ({
  type: NUM,
  number: value,
})

export const acNumOperation = () => ({
  type: AC,
})

export const evalNumOperation = (expression, result) => ({
  type: EVAL,
  expression,
  result,
})

export const plusNumOperation = () => ({
  type: PLUS,
})

export const multiplyNumOperation = () => ({
  type: MULTIPLY,
})

export const minusplyNumOperation = () => ({
  type: MINUS,
})

export const deplyNumOperation = () => ({
  type: DEPLY,
})

export const pointNumOperation = () => ({
  type: POINT,
})

export const persentNumOperation = () => ({
  type: PERSENT,
})

export const plusminNumOperation = () => ({
  type: PLUSMIN,
})

export const deleteHistory = (expression, result) => ({
  type: DELETEHISTORY,
  expression,
  result,
})

export const showRenewHistory = (history) => ({
  type: SHOWRENEWHISTORY,
  history
})
