import Fetch from '../scripts/dataFetch'
import deleteRequest from '../scripts/deleteHistory'
import OperationSign from '../scripts/PutAway'
import updateHistory from '../scripts/updateHistory'
import {
  AC,
  DELETEHISTORY,
  DEPLY,
  EVAL,
  MINUS,
  MULTIPLY,
  NUM,
  PERSENT,
  PLUS,
  PLUSMIN,
  POINT,
  SHOWRENEWHISTORY,
} from './actions/variables'

const initialState = {
  disabledPoint: false,
  updateNedeed: 0,
  addHistory: [],
  history: [],
  counter: '',
}

const numReducer = (state = initialState, action) => {
  const operationSign = new OperationSign()
  switch (action.type) {
    case NUM: {
      console.log(state.counter + action.counter)
      console.log(state.counter, action.counter)
      const storeCounter = action.counter
      return {
        ...state,
        addHistory: [],
        counter: storeCounter,
      }
    }
    case PLUS:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}+`,
      }
    case AC:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: '',
      }
    case MULTIPLY:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}*`,
      }
    case MINUS:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}-`,
      }
    case DEPLY:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: `${operationSign.putAway(state.counter)}/`,
      }
    case POINT:
      return {
        ...state,
        disabledPoint: true,
        addHistory: [],
        counter: `${operationSign.pointEnd(state.counter)}.`,
      }
    case PERSENT:
      return {
        ...state,
        addHistory: [],
        counter: `${operationSign.putAway(state.counter)}%`,
      }
    case PLUSMIN:
      return {
        ...state,
        addHistory: [],
        disabledPoint: false,
        counter: operationSign.plusMinOperation(state.counter),
      }
    case EVAL:
      Fetch('/', 'POST', state.counter, operationSign.putFirst(operationSign.endValue(state.counter)))
      // eslint-disable-next-line no-case-declarations
      const result = operationSign.putFirst(operationSign.endValue(state.counter))
      return {
        ...state,
        updateNedeed: state.updateNedeed + 1,
        disabledPoint: false,
        counter: result,
      }
    case DELETEHISTORY:
      console.log(action.expression, 1)
      deleteRequest(action.expression, action.result)
      return {
        ...state,
        updateNedeed: state.updateNedeed + 1,
        history: updateHistory(state.history),
      }
    case SHOWRENEWHISTORY:
      return {
        ...state,
        history: action.data,
      }
    default:
      return state
  }
}

export default numReducer
