import { call, put, takeEvery } from 'redux-saga/effects'
import { showRenewHistory } from '../redux/actions/actions'
import getHistoryApi from '../api/resendDataApi'
import { SHOWRENEWHISTORY } from '../redux/actions/variables'

function* showHistoryWorker() {
  const data = yield call(getHistoryApi)
  const history = yield call(() => new Promise((response) => response(data.json)))
  yield put(showRenewHistory(history))
}

function* showHistoryWatcher() {
  yield takeEvery(SHOWRENEWHISTORY, showHistoryWorker)
}

export default showHistoryWatcher
