import Box from '@mui/material/Box'
import React, { useCallback, useEffect, useState } from 'react'
import { makeStyles } from '@mui/styles'
import { Grid } from '@mui/material'
import Button from '@mui/material/Button'
import { observer } from 'mobx-react'
import { styled } from '@mui/material/styles'
import Paper from '@mui/material/Paper'
import operation from '../constans/dataRedux'
import { buttonOperations, buttons, buttonZero } from '../constans/colorSchemeButtons'
import operations from '../constans/operations'
import numericStore from '../store/numericStore'

const useStyles = makeStyles({
  display: {
    fontSize: 28,
    width: 'auto',
    whiteSpace: 'normal',
    textAlign: 'right',
    fontWeight: 300,
    height: 90,
  },
})

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
  border: '4px solid #1d2352',
  backgroundColor: '#1d2352',
}))

export const Calculator = observer(() => {
  const [disabled, setDisabled] = useState(false)
  const classes = useStyles()

  useEffect(() => {
    console.log(numericStore)
  })

  const checkPoint = useCallback((value) => {
    if (operations.includes(value)) setDisabled(false)
    else if (value === ',') setDisabled(true)
  }, [])

  const handleClick = useCallback((event, recovery) => function () {
    checkPoint(event)
    if (typeof event === 'number') numericStore.numericOperation(event)
    else if (recovery) console.log(recovery())
  }, [])

  const checkButton = useCallback((value) => {
    if (value === 0) return buttonZero
    if (typeof value === 'string') return buttonOperations
    return buttons
  }, [])

  const stateArray = useCallback(() => operation.map((val, i) => {
    const buttonSettings = checkButton(val.type)
    return (
      <Grid key={val.type} item xs={buttonSettings.size}>
        <Button
          sx={{
            bgcolor: buttonSettings.color,
            color: 'white',
            borderRadius: 2,
            borderColor: 'text.primary',
            gridRow: '5',
            width: '100%',
            gridColumn: 'span 2',
          }}
          disabled={val.type === ',' ? disabled : false}
          variant="outlined"
          size="large"
          onClick={handleClick(val.type, val.function)}
          key={i.toString()}
          className={val.classStyle}
        >
          {val.type}
        </Button>
      </Grid>
    )
  }), [disabled])

  return (
    <Item style={{ border: '4px solid #1d2352', padding: 5, backgroundColor: '#1d2352' }}>
      <Box
        className={classes.display}
        component="span"
        sx={{
          bgcolor: '#B0D6DE', border: 1, borderRadius: 4, display: 'block', color: 'black', marginBottom: 1,
        }}
      >
        {numericStore.counter}
      </Box>
      <Grid container rowSpacing={1} columnSpacing={{ xs: 1 }}>
        {stateArray()}
      </Grid>
    </Item>
  )
})

export default Calculator
