import React, { useEffect, useState } from 'react'
import { Grid } from '@mui/material'
import Button from '@mui/material/Button'
import DeleteIcon from '@mui/icons-material/Delete'
import Pagination from '@mui/material/Pagination'
import { useDispatch } from 'react-redux'
import { deleteHistory } from '../actions/actions'
import deleteAnimation from '../scripts/historyAnimationDelete'

const DateNow = new Date().toLocaleDateString()
const TimeNow = new Date().toLocaleTimeString()

let counterPages = 0
const historyResult = []

const History = ({ history }) => {
  const dispatch = useDispatch()
  const historyJSX = (history.counter.history.length > 0)
    ? history.counter.history.map((historyMap, index) => (
      // eslint-disable-next-line react/no-array-index-key
      <Grid item key={index} sx={{ gridColumn: 'span 2' }}>
        <div sx={{ fontSize: '12px', color: 'black' }}>
          {DateNow}
          {' '}
          {TimeNow}
          {' '}
          операция
          {' '}
          {historyMap.expression}
          =
          {historyMap.result}
        </div>
        <div>
          <Button variant="outlined" startIcon={<DeleteIcon />} color="error" onClick={deleteAnimation(dispatch, { type: deleteHistory, expression: historyMap.expression, result: historyMap.result })}>
            Delete
          </Button>
        </div>
      </Grid>
    )) : <div sx={{ fontSize: '12px', color: 'black' }}>Нет истории</div>

  useEffect(() => {
    console.log('>>>', historyResult)
    if (historyJSX.length / 5 === 1) {
      const addInHistoryResult = []
      for (let i = counterPages; i < historyJSX.length; i += 1) {
        addInHistoryResult.push(historyJSX[i])
      }
      counterPages += 5
      historyResult.push(addInHistoryResult)
    }
  }, [historyJSX])

  const [page, setPage] = useState(historyResult[0])
  const changesPages = (event, value) => {
    console.log(event)
    setPage(value)
  }
  return (
    <>
      <Grid container spacing={2} sx={{ display: 'grid', gridTemplateColumns: 'repeat(1, 1fr)' }}>
        {(historyResult.length < 5) ? historyJSX : page.map((h) => h)}
      </Grid>
      <Pagination defaultPage={1} onChange={changesPages} count={10} />
    </>
  )
}
export default History
